import React, { useState } from "react";
// @ts-ignore
import './css/leftnav.css';

function LeftNav() {
    const [isCollapsed, setIsCollapsed] = useState(false);
    const [sidebarWidth, setSidebarWidth] = useState(250); // Initial width
    const [isResizing, setIsResizing] = useState(false);

    const handleMouseDown = (e) => {
        setIsResizing(true);
    };

    const handleMouseMove = (e) => {
        if (isResizing && !isCollapsed) {
            const newWidth = Math.max(100, e.clientX); // Minimum width of 100px
            setSidebarWidth(newWidth);
        }
    };

    const handleMouseUp = () => {
        setIsResizing(false);
    };

    const toggleCollapse = () => {
        setIsCollapsed(!isCollapsed);
        if (isCollapsed) {
            setSidebarWidth(250);
        }
    };

    return (
        <div
            id="left-sidebar"
            style={{
                width: isCollapsed ? "50px" : `${sidebarWidth}px`,
            }}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        >
            <div id="left-text-container" style={{ display: isCollapsed ? "none" : "block" }}>
                <h4>Text Elements</h4>
                <h4>Lists</h4>
            </div>
            <div id="collapse-arrow" onClick={toggleCollapse}>
                <i
                    id="caret"
                    className={`fa ${isCollapsed ? "fa-caret-right" : "fa-caret-left"}`}
                />
            </div>
            {!isCollapsed && (
                <div
                    id="resizer"
                    onMouseDown={handleMouseDown}
                />
            )}
        </div>
    );
}

export default LeftNav;
