import React, { useState } from "react";
// @ts-ignore
import "./css/leftnav.css";

function LeftNav({ isCollapsed, setIsCollapsed }) {
    const [isTextOpen, setIsTextOpen] = useState(false);
    const [previewElement, setPreviewElement] = useState<string | null>(null);

    const handleCollapseToggle = () => {
        setIsCollapsed(!isCollapsed);
    };

    const renderPreview = (title: string) => {
        switch (title) {
            case "Title 1":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 1</div>;
            case "Title 2":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 2</div>;
            case "Title 3":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 3</div>;
            case "Title 4":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 4</div>;
            case "Title 5":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 5</div>;
            case "Title 6":
                return <div style={{ padding: "10px", border: "1px solid #ccc" }}>This is a preview of Title 6</div>;
            default:
                return <div>Select an element to see its preview</div>;
        }
    };

    return (
        <div
            id="left-nav"
            style={{
                width: isCollapsed ? "30px" : `300px`,
            }}
        >
            <div id="left-text-container" style={{ display: isCollapsed ? "none" : "block" }}>
                <h4 onClick={() => setIsTextOpen(!isTextOpen)}>
                    Text Elements{" "}
                    <i className={`fa ${!isTextOpen ? "fa-caret-down" : "fa-caret-up"}`} />
                </h4>
                {isTextOpen && (
                    <div className="dropdown">
                        {["Title 1", "Title 2", "Title 3", "Title 4", "Title 5", "Title 6"].map((title) => (
                            <div
                                key={title}
                                draggable
                                onMouseEnter={() => setPreviewElement(title)}
                                onMouseLeave={() => setPreviewElement(null)} // Clear the preview when not hovering
                            >
                                {title}
                            </div>
                        ))}
                    </div>
                )}

                <h4>Lists</h4>
            </div>

            <div id="collapse-arrow" onClick={handleCollapseToggle}>
                <i id="caret" className={`fa ${isCollapsed ? "fa-caret-right" : "fa-caret-left"}`} />
            </div>

            {/* Preview Section */}
            {!isCollapsed && previewElement && (
                <div id="preview-section" style={{ marginTop: "20px", padding: "10px", background: "#f9f9f9" }}>
                    <h5>Preview</h5>
                    {renderPreview(previewElement)}
                </div>
            )}
        </div>
    );
}

export default LeftNav;
